(function($){$.accordian=function(el,options){var base=this;base.$el=$(el);base.el=el;base.$el.data("accordian",base);base.init=function(){base.options=$.extend({},$.accordian.defaultOptions,options);base.active=$.accordian.defaultOptions.active.substring(1);base.$el.find($.accordian.defaultOptions.content).hide();base.$el.find($.accordian.defaultOptions.header).click(function(){base.open($(this).parent())})};base.open=function(el){if(el.hasClass(base.active)){base.close(el)}else{if(base.$active){base.close(base.$active)}base.$active=el;el.addClass(base.active);el.find($.accordian.defaultOptions.content).slideDown("fast")}};base.close=function(el){el.removeClass(base.active);el.find($.accordian.defaultOptions.content).slideUp("fast")};base.init()};$.accordian.defaultOptions={header:".accHeader",content:".accContent",active:".active"};$.fn.accordian=function(options){return this.each(function(){(new $.accordian(this,options))})}}(jQuery));